mapper.select:
select count(*)
from visits

	web_app = #{webApp}
	and creation_date >= #{fromDate}
	and creation_date <= #{toDate}

select count(*)
from visits

	web_app = #{webApp}
	and creation_date >= #{fromDate}
	and creation_date <= #{toDate}

group by user_id, device_id, location_id, visits.page, creation_date

choose.when:
select year(creation_date) as item, count(*)
				as value
				from visits

					web_app = #{webApp}
					and creation_date >= #{fromDate}
					and creation_date <= #{toDate}

				group by item

choose.otherwise:
select concat(monthname(creation_date), ', ', year(creation_date)) as item, count(*) as value
from visits

	web_app = #{webApp}
	and creation_date >= #{fromDate}
	and creation_date <= #{toDate}

group by item

choose.when:
select year(creation_date) as item, count(*) as value from visits
web_app = #{webApp} and target_action = 1 and creation_date >= #{fromDate} and creation_date <= #{toDate}
group by item

choose.otherwise:
select concat(monthname(creation_date), ', ', year(creation_date)) as item, count(*) as value
from visits

	web_app = #{webApp} and target_action = 1
	and creation_date >= #{fromDate}
	and creation_date <= #{toDate}

group by item

mapper.select:
select concat(monthname(creation_date), ', ', year(creation_date)) as item, avg(visit_time) as value
from visits

	web_app = #{webApp}
	and creation_date >= #{fromDate}
	and creation_date <= #{toDate}

group by item

select page as item, avg(visit_time) as value
from visits

	web_app = #{webApp}
	and creation_date >= #{fromDate}
	and creation_date <= #{toDate}

group by visits.page

select count(user_id) from (select user_id, creation_date, sum(up_total_time) as u_total_time from (select user_id, creation_date, page, sum(visit_time) as up_total_time from visits
web_app = #{webApp} and creation_date >= #{fromDate} and creation_date <= #{toDate}
group by user_id, creation_date, visits.page) as up_visits group by user_id, creation_date) as visit_times where u_total_time < 15.0
